import React from 'react'
import { withTranslation } from 'react-i18next'
import { Button, Modal, Input, Form } from 'antd'
import './ModalAssignRole.less'
import { newNotification, parseError } from '@utils'
import client, { M_EDIT_USER_NODE } from '@graphql'

class ModalEditUserInNode extends React.PureComponent {
  state = { visible: false }


  showModal = () => {
    this.setState({
      visible: true
    })
    this.props.form.resetFields()
  }

  handleOk = () => {
    const {
      props: { queryNode, node, user, i18n },
    } = this

    this.props.form.validateFields((err, values) => {
      if (!err) {
        const { firstname, lastname, newPassword, phoneNumber } = values
        client
          .mutate({
            mutation: M_EDIT_USER_NODE,
            variables: {
              userId: user._id,
              nodeId: node._id,
              input: {
                firstname,
                lastname,
                newPassword,
                phoneNumber
              }
            }
          }).then(({ data }) => {
            if (!data.editUserInNode) {
              throw new Error('group.editUserInNodeError')
            }

            newNotification(i18n.t('group.editUserInNodeSuccess'), 'success')
            queryNode(node._id)
            this.handleCancel()
          }).catch(err => {
            newNotification(i18n.t(parseError(err)), 'danger')
          })
      }
    })
  }

  handleCancel = () => {
    this.setState({
      visible: false
    })
  }

  render() {
    const {
      props: { i18n, user }
    } = this

    const { getFieldDecorator } = this.props.form

    return (
      <>

        <Button type='primary' ghost className='btn-update-user-node' onClick={this.showModal}>
          {i18n.t('common.edit')}
        </Button>


        <Modal
          visible={this.state.visible}
          onOk={this.handleOk}
          onCancel={this.handleCancel}
          className='modal-update-user-node'
          centered
        >
          <Form>
            <Form.Item label={i18n.t('user.username')}>
              {getFieldDecorator('username', {
                initialValue: user.username
              })(<Input disabled placeholder={i18n.t('user.username')} />)}
            </Form.Item>

            <Form.Item label={i18n.t('user.email')}>
              {getFieldDecorator('email', {
                initialValue: user.email
              })(<Input disabled placeholder={i18n.t('user.email')} />)}
            </Form.Item>

            <Form.Item label={i18n.t('user.firstname')}>
              {getFieldDecorator('firstname', {
                initialValue: user.firstname,
                rules: [
                  {
                    required: true,
                    message: i18n.t('require.requireFirstname')
                  }
                ]
              })(<Input placeholder={i18n.t('user.firstname')} />)}
            </Form.Item>

            <Form.Item label={i18n.t('user.lastname')}>
              {getFieldDecorator('lastname', {
                initialValue: user.lastname,
                rules: [
                  {
                    required: true,
                    message: i18n.t('require.requireLastname')
                  }
                ]
              })(<Input placeholder={i18n.t('user.lastname')} />)}
            </Form.Item>

            <Form.Item label={i18n.t('user.phoneNumber')}>
              {getFieldDecorator('phoneNumber', {
                initialValue: user.phoneNumber,
                rules: [
                  {
                    required: true,
                    message: i18n.t('require.requirePhone')
                  },
                  {
                    pattern: new RegExp('[+0-9]+'),
                    message: i18n.t('require.requirePhoneValid')
                  }
                ]
              })(<Input type='number' placeholder={i18n.t('user.phoneNumber')} />)}
            </Form.Item>

            <Form.Item label={i18n.t('user.newPassword')}>
              {getFieldDecorator('newPassword', {
              })(<Input type='password' placeholder={i18n.t('user.newPassword')} />)}
            </Form.Item>


          </Form>

        </Modal>
      </>
    )
  }
}

export default withTranslation()(Form.create({ name: 'edit-user-in-node' })(ModalEditUserInNode))
