import React from 'react'

import zoomInIcon from '@misc/images/annotate/zoom-in.svg'
import zoomOutIcon from '@misc/images/annotate/zoom-out.svg'

class Annotate extends React.PureComponent {
  componentDidUpdate() {
    this.refs['info-zoomIndex'].style.webkitAnimation = 'none'
    setTimeout(this.handleAnimation, 10)
  }

  handleAnimation = () => {
    this.refs['info-zoomIndex'].style.webkitAnimation = ''
  }

  render() {
    const { zoomIndex, handleZoomIn, handleZoomOut } = this.props
    return (
      <div className='wrapper-annotate'>
        <button type='button' disabled={zoomIndex > 1.4} className='btn-annotate' onClick={handleZoomIn}>
          <img src={zoomInIcon} alt='icon' />
        </button>
        <button type='button' disabled={zoomIndex < 0.6} className='btn-annotate' onClick={handleZoomOut}>
          <img src={zoomOutIcon} alt='icon' />
        </button>
        <span className='info-zoomIndex' ref='info-zoomIndex'>
          x{zoomIndex.toFixed(1)}
        </span>
      </div>
    )
  }
}

export default Annotate
