/* eslint-disable jsx-a11y/anchor-is-valid */
import React from 'react'
import { withTranslation } from 'react-i18next'
import { withRouter } from 'react-router-dom'
import { Form, Input, Button, Switch, Checkbox } from 'antd'
import ReactNotification from 'react-notifications-component'

import { inject, observer } from 'mobx-react'
import './layout.less'
import './signupv2.less'

import i18next from '../../translation/index'
import logo from '../../misc/logo/asset9.svg'
import { client, M_SIGN_UP, Q_EMAIL_SETTING } from '@graphql'
import { newNotification, returnError } from '@utils'

import ModalNotification from '@components/ModalNotification'

@withRouter
@inject(({ stores }) => stores)
@observer
class SignUp extends React.PureComponent {
  constructor(props) {
    super(props)
    this.state = {
      currentState: '',
      checked: true,
      confirmDirtyPassword: false,
      confirmDirtyEmail: false,
      visible: false,
      emailSettings: []
    }
  }

  componentDidMount() {
    this.setState({
      checked: localStorage.getItem('i18nextLng') === 'vi'
    })
  }

  handleChangeLanguage = checked => {
    i18next.changeLanguage(checked ? 'vi' : 'en')
    this.setState({
      checked
    })
  }

  queryEmailSetting = () => {
    client
      .query({
        query: Q_EMAIL_SETTING,
        fetchPolicy: 'network-only'
      })
      .then(({ data: { getEmailSetting } }) => {
        this.setState({ emailSettings: getEmailSetting })
      })
      .catch(error =>
        returnError(
          'Get conferences',
          {
            error,
            errorMessage: this.props.i18n.t('notification.queryEmailSettingError')
          },
          { container: 'top-right' }
        )
      )
  }

  handleSubmit = e => {
    e.preventDefault()
    this.props.form.validateFields((err, values) => {
      //MUTATION CREATE USER

      if (!err) {
        client
          .mutate({
            mutation: M_SIGN_UP,
            variables: {
              newUser: {
                username: values.username,
                password: values.password,
                firstname: values.firstname,
                lastname: values.lastname,
                email: values.email,
                phoneNumber: values.phoneNumber
              }
            }
          })
          .then(({ data: { signUp } }) => {
            this.props.form.resetFields()
            newNotification(this.props.i18n.t('notification.signupSuccess'), 'success', { container: 'top-right' })
          })
          .catch(errors => {
            if (errors && errors.message && /^Network error:/.test(errors.message)) {
              returnError(
                'Error',
                {
                  errorMessage: 'Error connect'
                },
                { container: 'top-right' }
              )
              return false
            }

            if (errors.graphQLErrors) {
              errors.graphQLErrors[0].message.split(', ').map(item => {
                if (item === 'EXTENSION_NOT_FOUND') {
                  this.props.form.resetFields()
                  this.showModal()
                } else {
                  returnError(
                    'Error',
                    {
                      errorMessage: this.props.i18n.t(item)
                    },
                    { container: 'top-right' }
                  )
                }
                return false
              })
              return false
            }

            if (errors.message) {
              returnError(
                'Error',
                {
                  errorMessage: errors.message
                },
                { container: 'top-right' }
              )
              return false
            }
          })
      }
    })
  }

  showModal = () => {
    this.queryEmailSetting()
    this.setState({
      visible: true,
    })
  }

  handleCancel = e => {
    this.setState({
      visible: false,
    })
  }

  render() {
    const {
      props: {
        i18n,
        form: { getFieldDecorator }
      }
    } = this

    return (
      <div className='container'>
        <ModalNotification
          visible={this.state.visible}
          handleCancel={this.handleCancel}
          emailSettings={this.state.emailSettings}
        />
        <div className='wrapper-notification'>
          <ReactNotification />
        </div>
        <div className='header'>
          <div className='container-header'>
            <div onClick={() => this.props.history.push('/')} className='logoHolder'>
              <img src={logo} alt='logo' />
            </div>
            <ul>
              <li>
                <Switch
                  className='switch-language'
                  checkedChildren='VI'
                  unCheckedChildren='EN'
                  checked={this.state.checked}
                  onClick={this.handleChangeLanguage}
                />
              </li>
            </ul>
          </div>
        </div>
        <div className='body'>
          <div className='nav-menu'>
            <p className='nav-menu-title'>{i18n.t('signup.accountRegistration')}</p>
          </div>
          <div className='content-body' style={{ padding: '0px 51px 27px 71px' }}>

            <Form onSubmit={this.handleSubmit} ref='form' className='signup'>
              <div className='group-form-item'>
                <div className='left'>{i18n.t('signup.personalInformation')}</div>

                <div className='right'>
                  <div className='row'>
                    <Form.Item label={i18n.t('user.username')}>
                      {getFieldDecorator('username', {
                        rules: [
                          {
                            required: true,
                            message: i18n.t('require.requireUsername')
                          },
                          {
                            min: 5,
                            message: i18n.t('signup.pleaseAtLeast5Characters')
                          }
                        ]
                      })(<Input placeholder={i18n.t('require.requireUsername')} />)}
                    </Form.Item>
                  </div>

                  <div className='row'>
                    <Form.Item label={i18n.t('user.firstname')}>
                      {getFieldDecorator('firstname', {
                        rules: [
                          {
                            required: true,
                            message: i18n.t('require.requireFirstname')
                          },
                          {
                            // eslint-disable-next-line max-len
                            pattern: /^[a-zA-ZÀÁÂÃÈÉÊẾÌÍÒÓÔÕÙÚĂĐĨŨƠàáâãèéêếìíòóôõùúăđĩũơƯĂẠẢẤẦẨẪẬẮẰẲẴẶẸẺẼỀỀỂưăạảấầẩẫậắằẳẵặẹẻẽềềểỄỆỈỊỌỎỐỒỔỖỘỚỜỞỠỢỤỦỨỪễệỉịọỏốồổỗộớờởỡợụủứừỬỮỰỲỴÝỶỸửữựỳýỵỷỹ\s]+$/,
                            message: i18n.t('signup.invalidCharacterNum')
                          }
                        ]
                      })(<Input placeholder={i18n.t('require.requireFirstname')} />)}
                    </Form.Item>

                    <Form.Item label={i18n.t('user.lastname')}>
                      {getFieldDecorator('lastname', {
                        rules: [
                          {
                            required: true,
                            message: i18n.t('require.requireLastname')
                          },
                          {
                            // eslint-disable-next-line max-len
                            pattern: /^[a-zA-ZÀÁÂÃÈÉÊẾÌÍÒÓÔÕÙÚĂĐĨŨƠàáâãèéêếìíòóôõùúăđĩũơƯĂẠẢẤẦẨẪẬẮẰẲẴẶẸẺẼỀỀỂưăạảấầẩẫậắằẳẵặẹẻẽềềểỄỆỈỊỌỎỐỒỔỖỘỚỜỞỠỢỤỦỨỪễệỉịọỏốồổỗộớờởỡợụủứừỬỮỰỲỴÝỶỸửữựỳýỵỷỹ]+$/,
                            message: i18n.t('signup.invalidCharacterNum')
                          }
                        ]
                      })(<Input placeholder={i18n.t('require.requireLastname')} />)}
                    </Form.Item>
                  </div>

                  <div className='row'>
                    <Form.Item label={i18n.t('user.phone')}>
                      {getFieldDecorator('phoneNumber', {
                        rules: [
                          {
                            required: true,
                            message: i18n.t('require.requirePhone')
                          },
                          {
                            pattern: new RegExp('[+0-9]+'),
                            message: i18n.t('require.requirePhoneValid')
                          }
                        ]
                      })(<Input placeholder={i18n.t('require.requirePhone')} />)}
                    </Form.Item>
                  </div>
                </div>
              </div>

              <div className='group-form-item'>
                <div className='left'>{i18n.t('signup.emailTitle')}</div>

                <div className='right'>
                  <div className='row'>
                    <Form.Item>
                      {getFieldDecorator('email', {
                        rules: [
                          {
                            required: true,
                            message: i18n.t('require.requireEmail')
                          },
                          {
                            validator: (rule, value, callback) => {
                              const { form } = this.props
                              if (value && this.state.confirmDirtyEmail) {
                                form.validateFields(['confirmEmail'], { force: true })
                              }
                              callback()
                            }
                          }
                        ]
                      })(<Input type='email' placeholder={i18n.t('require.pleaseEnterEmail')} />)}
                    </Form.Item>
                  </div>

                  <div className='row'>
                    <Form.Item>
                      {getFieldDecorator('confirmEmail', {
                        rules: [
                          {
                            required: true,
                            message: i18n.t('require.requireEmail')
                          },
                          {
                            validator: (rule, value, callback) => {
                              const { form } = this.props
                              if (value && value !== form.getFieldValue('email')) {
                                callback(i18n.t('signup.twoEmailsInconsistent'))
                              } else {
                                callback()
                              }
                            }
                          }
                        ]
                      })(
                        <Input
                          onBlur={e =>
                            this.setState({ confirmDirtyEmail: this.state.confirmDirtyEmail || !!e.target.value })
                          }
                          type='email'
                          placeholder={i18n.t('require.pleaseEnterConfirmEmail')}
                        />
                      )}
                    </Form.Item>
                  </div>
                </div>
              </div>

              <div className='group-form-item'>
                <div className='left'>{i18n.t('signup.signInPassword')}</div>
                <div className='right'>
                  <div className='row'>
                    <Form.Item>
                      {getFieldDecorator('password', {
                        rules: [
                          {
                            required: true,
                            message: i18n.t('require.requirePassword')
                          },
                          {
                            validator: (rule, value, callback) => {
                              const { form } = this.props
                              if (value && this.state.confirmDirtyPassword) {
                                form.validateFields(['confirmPassword'], { force: true })
                              }
                              callback()
                            }
                          }
                        ]
                      })(<Input type='password' placeholder={i18n.t('require.pleaseEnterPassword')} />)}
                    </Form.Item>
                  </div>

                  <div className='row'>
                    <Form.Item>
                      {getFieldDecorator('confirmPassword', {
                        rules: [
                          {
                            required: true,
                            message: i18n.t('require.requirePassword')
                          },
                          {
                            validator: (rule, value, callback) => {
                              const { form } = this.props
                              if (value && value !== form.getFieldValue('password')) {
                                callback(i18n.t('signup.twoPasswordsInconsistent'))
                              } else {
                                callback()
                              }
                            }
                          }
                        ]
                      })(
                        <Input
                          onBlur={e =>
                            this.setState({ confirmDirtyPassword: this.state.confirmDirtyPassword || !!e.target.value })
                          }
                          type='password'
                          placeholder={i18n.t('require.pleaseEnterConfirmPassword')}
                        />
                      )}
                    </Form.Item>
                  </div>

                  <div className='note'>
                    <p className='note-title'>{i18n.t('signup.passwordMust')}</p>
                    <p>{i18n.t('signup.haveAtLeast8Characters')}</p>
                    <p>{i18n.t('signup.haveAtLeast1LetterABC')}</p>
                    <p>{i18n.t('signup.haveAtLeast1Number123')}</p>
                    <p>{i18n.t('signup.includeBothUpperCaseAndLowerCaseCharacters')}</p>
                  </div>

                  <div className='note'>
                    <p className='note-title'>{i18n.t('signup.passwordMustNot')}</p>
                    <p>{i18n.t('signup.containOnlyOneCharacter1111111oraaaaaa')}</p>
                    <p>{i18n.t('signup.containOnlyConsecutiveCharacters12345678orAbcdefgh')}</p>
                  </div>
                </div>
              </div>

              <div className='group-form-item' style={{ border: '0px' }}>
                <div className='left' />
                <div className='right'>
                  <div className='row-confirm-policy'>
                    <Form.Item>
                      {getFieldDecorator('agree', {
                        rules: [
                          {
                            required: true,
                            message: i18n.t('signup.mustAcceptTerms')
                          }
                        ]
                      })(
                        <Checkbox>
                          {i18n.t('signup.iAgree')} <b>MD Conference</b> <a>{i18n.t('signup.termsOfService')}</a> {i18n.t('signup.and')}{' '}
                          <a>{i18n.t('signup.privacyPolicy')}</a>
                        </Checkbox>
                      )}
                    </Form.Item>
                  </div>

                  <div className='row-btn-signup'>
                    <Button type='primary' htmlType='submit' className='signup-form-button'>
                      {i18n.t('signup.signUp')}
                    </Button>
                  </div>
                </div>
              </div>
            </Form>
          </div>
        </div>
      </div>
    )
  }
}

export default Form.create()(withTranslation()(SignUp))
