import React from 'react'
import { Button, Avatar, Icon, Tooltip } from 'antd'
import { newNotification } from '@utils'
import { client, M_APPROVE_QNA } from '@graphql'
import { AvatarComponent } from '@components'

class QnAApprovalViewItem extends React.Component {
  constructor(props) {
    super(props)
    this.state = {}
  }

  handleApproveQNA = (qnaId, value) => {
    client
      .mutate({
        mutation: M_APPROVE_QNA,
        variables: {
          newQNAApproveInput: {
            qnaId,
            value
          }
        }
      })
      .then(({ data: { approveQNA } }) => {
        if (approveQNA) {
          return newNotification('Thành công! ', 'success', {
            animationIn: ['animated', 'fadeIn']
          })
        }
      })
      .catch(error => {
        return newNotification('Please try again later!', 'danger')
      })
  }

  render() {
    const {
      props: {
        qnaItem: {
          question,
          _id: qnaId,
          isAnonymous,
          createdBy,
          createdBy: { firstname, lastname }
        }
      }
    } = this
    return (
      <div className='qnaApprovalItemView'>
        <div className='usernameWrapper'>
          <span className='usernameHolder'>
            {isAnonymous ? (
              <>
                <Tooltip placement='bottom' title='Người dùng ẩn danh'>
                  <Avatar size='small' icon='eye-invisible' />
                </Tooltip>{' '}
                Người dùng ẩn danh:
              </>
            ) : (
              <>
                <AvatarComponent {...createdBy} size='small' /> {firstname} {lastname}:
              </>
            )}
          </span>

          <div className='approveButtonHolder'>
            <Button type='danger' onClick={() => this.handleApproveQNA(qnaId, false)} size='small'>
              <Icon type='close' /> Huỷ bỏ
            </Button>

            <Button type='primary' onClick={() => this.handleApproveQNA(qnaId, true)} size='small'>
              <Icon type='check' /> Duyệt
            </Button>
          </div>
        </div>

        <span className='question'>{question}</span>
      </div>
    )
  }
}

export default QnAApprovalViewItem
