import React from 'react'
import { inject, observer } from 'mobx-react'
import Tour from 'reactour'

const steps = [
  {
    selector: '#micro-button',
    content: 'Press and hold `Z` key to use Push-to-talk feature!',
    position: 'top'
  }
]

@inject(({ stores }) => stores)
@observer
class ReactTour extends React.PureComponent {
  state = { isTourOpen: false }

  componentDidMount() {
    this.setState({ isTourOpen: !localStorage.getItem('tourShowed') })
  }

  onCloseTour = () => {
    localStorage.setItem('tourShowed', 'true')
    this.setState({ isTourOpen: false })
  }

  render() {
    const { jitsiStore } = this.props

    if (jitsiStore.isConnected && jitsiStore.isJoined)
      return (
        <Tour
          steps={steps}
          isOpen={this.state.isTourOpen}
          onRequestClose={this.onCloseTour}
          accentColor='#5d9c5a'
          rounded={5}
          showButtons={false}
          showCloseButton={false}
          showNavigation={false}
          showNavigationNumber={false}
          showNumber={false}
          nextButton={null}
        />
      )
    return <React.Fragment />
  }
}

export default ReactTour
