import React from 'react'
import { Table, Button } from 'antd'
import { withTranslation } from 'react-i18next'
import moment from 'moment'

import ModalDelete from '../components/ModalDelete'

class Webinar extends React.PureComponent {
  formatNumber = e => (e < 10 ? '0' + e : e.toString())

  formatWeekday = e => {
    const arrayDay = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday']
    if (
      moment(e).year() === moment().year() &&
      moment(e).month() === moment().month()
    ) {
      if (moment(e).date() === moment().date()) {
        return 'Today'
      }
      if (moment(e).date() === moment().date() - 1) {
        return 'Yesterday'
      }
      if (moment(e).date() === moment().date() + 1) {
        return 'Tomorrow'
      }
    }
    return arrayDay[moment(e).day()]
  }

  processTime = e => {
    return e
      ? (
        <span className='timeHolder'>
          <span>{this.formatWeekday(e)}</span>
          <span>
            {this.formatNumber(moment(e).hour())}:
          {this.formatNumber(moment(e).minute())}
          </span>
        </span>
      )
      : (
        <span style={{ color: 'black' }}>Always Available</span>
      )
  }

  render() {
    const {
      webinars,
      handleJoinConference,
      handleDeleteItem,
      i18n
    } = this.props

    const rowData = webinars.map(e => {
      return {
        key: e._id,
        _id: e._id,
        topic: (
          <span
            className='clickableSpan'
            onClick={() => this.props.handleSelectWebinar(e)}
          >
            {e.name}
          </span>
        ),
        startAt: this.processTime(e.startAt),
        timeStamp: e.startAt,
        rawStartAt: moment(e.startAt).format('MMM Do YYYY, hh:mm a'),
        name: e.name
      }
    })

    const columns = [
      {
        title: i18n.t('startTime'),
        dataIndex: 'startAt',
        sorter: (a, b) => a.timeStamp - b.timeStamp,
        sortDirections: ['descend']
      },
      {
        title: i18n.t('topic'),
        dataIndex: 'topic',
        sorter: (a, b) => a.name.length < b.name.length,
        sortDirections: ['descend']
      },
      {
        title: 'Webinar ID',
        dataIndex: '_id'
      },
      {
        title: '',
        key: 'x',
        render: (e, i) => (
          <span className='columnBtnHolder'>
            <Button className='btn-start' onClick={() => handleJoinConference(i.key)}>{i18n.t('start')}</Button>
            <ModalDelete
              handleDeleteItem={handleDeleteItem}
              itemToDelete={i}
              type='webinar'
              title={i18n.t('deleteWebinar')}
              textButtonDelete={i18n.t('delete')}
            />
          </span>
        )
      }
    ]

    return (
      <div className='wrapper-webinar'>
        <Button
          type='primary'
          className='btn-create'
          onClick={() =>
            this.props.handleChangeDisplay('CREATE', i18n.t('createWebinar'))
          }
        >
          {i18n.t('createWebinar')}
        </Button>

        <Table className='common-table' columns={columns} dataSource={rowData} />
      </div>
    )
  }
}

export default withTranslation()(Webinar)
