import React from 'react'
import { Icon, Tooltip } from 'antd'

class UserQuestions extends React.Component {
  constructor(props) {
    super(props)
    this.state = {}
  }

  render() {
    const {
      props: {
        myUserId,
        activeQnA: { QNA }
      }
    } = this
    return (
      <div className='userQuestion'>
        <div className='title'>Câu hỏi của bạn:</div>

        {QNA && (
          <div className='userQuestionWrapper'>
            {QNA.filter(({ createdBy: { _id } }) => _id === myUserId).map(({ question, userAnswer }, index) => (
              <div key={index} className='userQuestionItem'>
                <span className='question'>{question}</span>
                <span className='answer'>
                  <span>Trả lời: </span>
                  {userAnswer ? userAnswer.answer : ''}{' '}
                  {userAnswer.isPrivate && (
                    <Tooltip placement='bottom' title='Chỉ bạn có thể thấy'>
                      <Icon type='info-circle' />
                    </Tooltip>
                  )}
                </span>
              </div>
            ))}
          </div>
        )}
      </div>
    )
  }
}

export default UserQuestions
