import React from 'react'
import { withTranslation } from 'react-i18next'
import { inject, observer } from 'mobx-react'
import { Tabs } from 'antd';
import './shareFileCollapse.less'
import MyFiles from './MyFiles';
import AllFiles from './AllFiles';

const { TabPane } = Tabs;


@inject(({ stores }) => stores)
@observer
class ShareFileCollapse extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
    }
  }

  handleCloseContext = () => {
    this.refs['menu-context'].style.display = 'none'
  }

  componentDidMount() {
    document.addEventListener('mousedown', this.handleCloseContextMenu)
  }

  handleCloseContextMenu = event => {
    if (this.refs['menu-context'] && !this.refs['menu-context'].contains(event.target)) {
      this.handleCloseContext()
    }
  }


  render() {
    const { conferenceState, } = this.props

    const selectedTabs = [
      conferenceState.isShowPoll,
      conferenceState.isShowPollManagement,
      conferenceState.isShowParticipants,
      conferenceState.isShowMessages,
      conferenceState.isShowAttendancesManagement,
      conferenceState.isShowQnAManagement,
      conferenceState.isShowQnA
    ].filter(e => e).length

    return (
      <div className='share-file-container' style={{ height: `calc(100vh - ${selectedTabs * 20 + 1}px)` }}>
        <Tabs
          tabBarStyle={{
            display: 'flex',
            justifyContent: 'center'
          }}
          defaultActiveKey="1"
        >
          <TabPane
            tab="All files" key="allfiles">
            <AllFiles />
          </TabPane>

          <TabPane tab="My files" key="myfiles">
            <MyFiles></MyFiles>
          </TabPane>
        </Tabs>


      </div>
    )
  }
}

export default withTranslation()(ShareFileCollapse)
