import React from 'react'
import { withTranslation } from 'react-i18next'
import {
  Form,
  Input,
  Select,
  TimePicker,
  DatePicker,
  Button,
  Checkbox,
  Radio,
  Col,
  Row
} from 'antd'
import moment from 'moment'
import './CreateWebinar.less'


import { client, M_UPDATE_WEBINAR } from '@graphql'
import { returnError, newNotification } from '@utils'

const { Option } = Select

const options = {
  QnA: 'A',
  practiceSession: 'B',
  authenticatedOnly: 'C',
  automaticallyRecord: 'D'
}

class FormEditWebinar extends React.Component {
  state = {
    alwaysAvailable: false,
  }

  convertTime = (date) => {
    const newDate = new Date(
      date._d.getFullYear(),
      date._d.getMonth(),
      date._d.getDate(),
    )
    return newDate.getTime()
  }

  convertDate = (date, time) => {
    const newDate = new Date(
      date._d.getFullYear(),
      date._d.getMonth(),
      date._d.getDate(),
      time._d.getHours(),
      time._d.getMinutes(),
      time._d.getSeconds()
    )
    return newDate.getTime()
  }

  handleSubmit = e => {
    e.preventDefault()
    if (this.state.alwaysAvailable)
      this.props.form.setFieldsValue({ date: null, time: null })
    this.props.form.validateFields((err, values) => {
      const startAt =
        values.date && values.time
          ? this.convertDate(values.date, values.time)
          : null

      const QnA = values['default-option'].indexOf('A') !== -1
      const practiceSession = values['default-option'].indexOf('B') !== -1
      const authenticatedOnly = values['default-option'].indexOf('C') !== -1
      const automaticallyRecord = values['default-option'].indexOf('D') !== -1
      //MUTATION CREATE CONFERENCE
      client
        .mutate({
          mutation: M_UPDATE_WEBINAR,
          variables: {
            webinarId: this.props.webinarSelect._id,
            newWebinarUpdate: {
              name: values.name,
              password: values.password || '',
              startAt: startAt - this.convertTime(values.date),
              startDate: this.convertTime(values.date),
              defaultParticipantRole: values.role,
              hostVideo: values['radio-host'] === 'A',
              panelistsVideo: values['radio-panelists'] === 'A',
              QnA,
              practiceSession,
              authenticatedOnly,
              automaticallyRecord
            }
          }
        })
        .then(({ data: { updateWebinar } }) => {
          this.props.form.resetFields()
          if (updateWebinar) {
            this.props.refetch()
            this.props.backToDefault()
            newNotification('Create Webinar Success', 'success', { container: 'top-right' })
          }
        })
        .catch(error =>
          returnError(
            'Get Link',
            {
              error,
              errorMessage: 'Cannot create conference, try again later!'
            },
            { container: 'top-center' }
          )
        )
    })
  }


  changeSeclect = ({ QnA, practiceSession, authenticatedOnly, automaticallyRecord }) => {
    return [
      options[QnA ? 'QnA' : null],
      options[practiceSession ? 'practiceSession' : null],
      options[authenticatedOnly ? 'authenticatedOnly' : null],
      options[automaticallyRecord ? 'automaticallyRecord' : null]
    ].filter(e => e !== undefined)
  }

  render() {
    const {
      props: {
        form: { getFieldDecorator },
        webinarSelect
      }
    } = this

    return (
      <Form
        ref='form'
        className='form-create-webinar'
        style={{ width: '60%' }}
        onSubmit={this.handleSubmit}
      >
        <h3>Edit a Webinar</h3>

        <Form.Item label='Webinar Name'>
          {getFieldDecorator('name', {
            initialValue: webinarSelect.name || ' ',
            rules: [
              {
                required: true,
                message: 'Topic!'
              }
            ]
          })(<Input style={{ width: '100%' }} placeholder='The Webinar 8' />)}
        </Form.Item>

        <Form.Item label='Default'>
          <Checkbox onChange={(e) => this.setState({ alwaysAvailable: e.target.checked })}>Always available</Checkbox>
        </Form.Item>

        <Form.Item label='Start Date' style={{ flex: 1, marginRight: '0.5rem' }}>
          {getFieldDecorator('date', {
            initialValue: moment(moment(webinarSelect.startAt).format('YYYY/MM/DD'), 'YYYY/MM/DD')
          })(<DatePicker disabled={this.state.alwaysAvailable} />)}
        </Form.Item>

        <Form.Item label='Start Time' style={{ flex: 1, marginLeft: '0.5rem' }}>
          {getFieldDecorator('time', {
            initialValue: moment(moment(webinarSelect.startAt).format('HH:mm:ss'), 'HH:mm:ss')
          })(
            <TimePicker
              disabled={this.state.alwaysAvailable}
              defaultOpenValue={moment('00:00:00', 'HH:mm:ss')}
              style={{ width: '100%' }}
            />
          )}
        </Form.Item>

        <Form.Item label='Password'>
          {getFieldDecorator('password')(
            <Input type='password' style={{ width: '100%' }} placeholder='12345678' />
          )}
        </Form.Item>

        <Row>
          <Col span={8} style={{ lineHeight: '40px' }}>Video</Col>
          <Col span={16}>
            <Form.Item label="Host" style={{ padding: '0px 0px 0px 5px' }}>
              {getFieldDecorator('radio-host', {
                initialValue: webinarSelect.hostVideo ? 'A' : 'B'
              })(
                <Radio.Group>
                  <Radio value="A">On</Radio>
                  <Radio value="B">Off</Radio>
                </Radio.Group>,
              )}
            </Form.Item>
            <Form.Item label="Panelists" style={{ padding: '0px 0px 0px 5px' }}>
              {getFieldDecorator('radio-panelists', {
                initialValue: webinarSelect.panelistsVideo ? 'A' : 'B'
              })(
                <Radio.Group>
                  <Radio value="A">On</Radio>
                  <Radio value="B">Off</Radio>
                </Radio.Group>,
              )}
            </Form.Item>
          </Col>
        </Row>

        <Form.Item label="Webinar Options">
          {getFieldDecorator('default-option', {
            initialValue: this.changeSeclect(webinarSelect)
          })(
            <Checkbox.Group style={{ width: '100%' }}>
              <Checkbox value="A">Q&A</Checkbox>
              <Checkbox value="B">Enable Practice Session</Checkbox>
              <Checkbox value="C">Only authenticated users can join</Checkbox>
              <Checkbox value="D">Record the webinar automatically</Checkbox>
            </Checkbox.Group>,
          )}
        </Form.Item>

        <Form.Item label='User Role'>
          {getFieldDecorator('role', { initialValue: webinarSelect.defaultParticipantRole })(
            <Select style={{ width: '100%' }}>
              <Option value='MODERATOR'>Owner</Option>
              <Option value='PARTICIPANT'>Participants</Option>
            </Select>
          )}
        </Form.Item>

        <div
          style={{ display: 'flex', flexDirection: 'row' }}
        >
          <Form.Item style={{ flex: 1, marginRight: '0.5rem', display: 'flex', justifyContent: 'flex-end' }}>

            {/* <Button
              onClick={backToDefault}
              className='login-form-button'
              style={{ width: '10rem' }}
            >
              Huá»·
            </Button> */}
          </Form.Item>
          <Form.Item style={{ flex: 1, marginLeft: '0.5rem' }}>
            <Button
              type='primary'
              style={{ width: '10rem', height: '38px', borderRadius: '8px', fontWeight: '600px' }}
              htmlType='submit'
              className='login-form-button'
            >
              Save
          </Button>
          </Form.Item>
        </div>

      </Form>
    )
  }
}

export default Form.create({ name: 'normal_login' })(withTranslation()(FormEditWebinar))
