import React, { Component } from 'react'

class EmailSettingComponent extends Component {
  state = {
    emailLanguage: 'English',
    // emailContact: {
    //   name: '',
    //   email: ''
    // },
    emailPanelists: true,
    emailConfirm: true,
    reminderEmail: null,
    followUpEmailAttendees: false,
    followUpEmailAbsentees: false
  }
  render() {
    return (
      <div className='emailSettingComp'>
        <div id='emailLanguage'>
          Select Email Language: {this.state.emailLanguage}
          <span>Edit</span>
        </div>

        <div id='emailContact'>
          Email Contact:
          <span>Edit</span>
        </div>

        <div id='emailPanelistsSetting'>
          {this.state.emailPanelists
            ? 'Invitation Email to Panelists'
            : 'No Invitation Email to Panelists'}
          <span>Edit</span>
        </div>

        <div id='emailConfirmSetting'>
          {this.state.emailConfirm
            ? 'Confirmation Email to Registrants'
            : 'No Confirmation Email to Registrants'}
          <span>Edit</span>
        </div>

        <div id='reminderEmail'>
          {this.state.reminderEmail === null
            ? 'No reminder email to Attendees and Panelists'
            : 'N/A reminder email to Attendees and Panelists'}
          <span>Edit</span>
        </div>

        <div id='followUpEmailAttendees'>
          {this.state.followUpEmailAttendees
            ? 'Follow-up email to Attendees'
            : 'No follow-up email to Attendees'}
          <span>Edit</span>
        </div>

        <div id='followUpEmailAbsentees'>
          {this.state.followUpEmailAbsentees
            ? 'Follow-up email to Absentees'
            : 'No follow-up email to Absentees'}
          <span>Edit</span>
        </div>
      </div>
    )
  }
}

export default EmailSettingComponent
